// Extract all assessments
const assessments = $input.all().map(item => item.json);

// Fields you want to calculate averages for
const fieldsToAverage = [
  "PdiWithScoringAvg",
  "Putt3ft",
  "Putt10ft",
  "Putt30ft",
  "Chip3yd",
  "Chip10yd",
  "Chip20yd",
  "Driver",
  "SixIronBFC",
  "BroadJump",
  "ASLRLeft",
  "ASLRRight",
  "DeepSquat",
  "SupineBridgeLeft",
  "SupineBridgeRight",
  "ScoringAverage"
];

// Helper function to calculate averages for a set of assessments
function calculateAverages(data) {
  const sums = {};
  const averages = {};

  fieldsToAverage.forEach(field => {
    sums[field] = 0;
  });

  data.forEach(assessment => {
    fieldsToAverage.forEach(field => {
      if (typeof assessment[field] === 'number') {
        sums[field] += assessment[field];
      }
    });
  });

  fieldsToAverage.forEach(field => {
    averages[field] = +(sums[field] / data.length).toFixed(2);
  });

  return averages;
}

// Get the most recent and last 3 assessment
const lastThree = assessments.slice(0, 3);

// Calculate averages
const averageScores = calculateAverages(assessments);
const averageOfLastThree = calculateAverages(lastThree);

// Return the final result
return {
  //assessments,
  //mostRecentAssessment,
  averageScores,
  averageOfLastThree
};
